#include "Castilla.h"//#include "../CapacitiveSensor/CapacitiveSensor.h"CapacitiveSwitch::CapacitiveSwitch(int pin_in,int pin):Button(pin,HIGH),sensor(pin_in,pin){	}void CapacitiveSwitch::config(int threashold){	this->threashold=threashold;}void CapacitiveSwitch::test(){	Serial.println(getValue());}long CapacitiveSwitch::getValue(){	return sensor.capacitiveSensor(30);}bool CapacitiveSwitch::getState(){	return getValue()>threashold;}